export const wrap = (input) => typeof input !== 'string' || input.length === 1
    ? input.toString()
    : `(?:${input})`;
export const concat = (...inputs) => inputs.join('');
export const optional = (input) => `${wrap(input)}?`;
export const repeat = (input, count, max) => max === undefined
    ? `${wrap(input)}{${count}}`
    : max === Infinity
        ? count > 1
            ? `${wrap(input)}{${count},}`
            : count < 1
                ? `${wrap(input)}*`
                : `${wrap(input)}+`
        : `${wrap(input)}{${count},${max}}`;
